<?php

/**
 * Koryukan_Db_StoreItemTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Koryukan_Db_StoreItemTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object Koryukan_Db_StoreItemTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Koryukan_Db_StoreItem');
    }

    /**
     * Load the items
     *
     * @return void
     */
    public function getItems($status)
    {
        $query = $this->createQuery('items');
        $query->innerJoin('items.StoreItemDetail');

        if (strlen($status) > 0) {
            $query->addWhere('Status = ?', $status);
        }

        $query->addOrderBy('id ASC');

        return $query;
    }
}