<?php

/**
 * Koryukan_Db_UserGroupTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class Koryukan_Db_UserGroupTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object Koryukan_Db_UserGroupTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Koryukan_Db_UserGroup');
    }

    /**
     * Return all the user groups
     *
     * @return Doctrine_Query
     */
    public function getAllUserGroups()
    {
        $query = $this->createQuery('groups');
        $query->leftJoin('groups.SubGroups');
        $query->orderBy('parentGroupId');
        //echo $query->getSqlQuery() . '<br>';

        return $query;
    }
}